SELECT /*+ LEADING(t1) USE_NL(t2) index_desc(t1 pk_board) */ * FROM board t1 INNER JOIN category t2 ON t1.category_no = t2.category_no WHERE t1.user_no = #{cri.user_no}
AND t1.category_no = #{cri.category}
AND REGEXP_LIKE(t1.title, #{cri.keyword}, 'i')
AND REGEXP_LIKE(t1.title, #{cri.keyword}, 'i') OR REGEXP_LIKE(t1.text, #{cri.keyword}, 'i')
OFFSET (#{cri.currPage} - 1) * #{cri.amount} ROWS FETCH NEXT #{cri.amount} ROWS ONLY

SELECT /* index_desc(board) */ count(board_no) FROM board WHERE user_no = #{cri.user_no}
AND category_no = #{cri.category}
AND REGEXP_LIKE(title, #{cri.keyword}, 'i')
AND REGEXP_LIKE(title, #{cri.keyword}, 'i') OR REGEXP_LIKE(text, #{cri.keyword}, 'i')
